	(11315,73,'employee_selected_image_url','brianschwartz-selected.jpg'),
	(11316,73,'employee_name','Glenn Steigerwald'),
	(11317,73,'employee_title','Web Development & User Experience'),
	(11318,73,'employee_description','Glenn uses his in-depth knowledge of all things code to produce custom, responsive websites that are made of, as he claims “just 0’s and 1’s”. When Glenn’s not lying about code, he’s training his horse to win races.'),
