	(11297,73,'employee_selected_image_url','benstuart-selected.jpg'),
	(11298,73,'employee_name','Bill Paffas'),
	(11299,73,'employee_title','CRO & User Experience'),
	(11300,73,'employee_description','As resident CRO expert, Bill combines his years of digital analytics experience with a keen understanding of user experience. When not testing websites, he often performs multivariate tests on his two dogs: Ralph & Rufus.'),
